@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700;800&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(195, 100%, 95%); /* Light sky blue background */
  --foreground: hsl(220, 30%, 25%); /* Deep blue text */
  --card: hsl(45, 100%, 96%); /* Cream yellow cards */
  --card-foreground: hsl(220, 30%, 25%);
  --popover: hsl(45, 100%, 96%);
  --popover-foreground: hsl(220, 30%, 25%);
  --primary: hsl(280, 100%, 70%); /* Bright purple primary */
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(120, 60%, 85%); /* Light green secondary */
  --secondary-foreground: hsl(220, 30%, 25%);
  --muted: hsl(60, 100%, 90%); /* Light yellow muted */
  --muted-foreground: hsl(220, 20%, 50%);
  --accent: hsl(30, 100%, 85%); /* Light orange accent */
  --accent-foreground: hsl(220, 30%, 25%);
  --destructive: hsl(0, 80%, 65%); /* Soft red */
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(280, 50%, 80%); /* Purple borders */
  --input: hsl(45, 100%, 92%); /* Light yellow inputs */
  --ring: hsl(280, 100%, 70%);
  --chart-1: hsl(280, 70%, 60%); /* Purple */
  --chart-2: hsl(120, 60%, 50%); /* Green */
  --chart-3: hsl(45, 90%, 60%); /* Yellow */
  --chart-4: hsl(200, 80%, 60%); /* Blue */
  --chart-5: hsl(340, 75%, 60%); /* Pink */
  --radius: 1.5rem; /* More rounded for childlike feel */

  /* Custom childlike theme colors */
  --light-primary: hsl(195, 100%, 95%);
  --light-secondary: hsl(45, 100%, 96%);
  --light-tertiary: hsl(120, 60%, 85%);
  --accent-green: hsl(120, 60%, 45%); /* Playful green */
  --accent-red: hsl(0, 80%, 65%); /* Soft red */
  --accent-purple: hsl(280, 100%, 70%); /* Bright purple */
  --accent-orange: hsl(30, 100%, 65%); /* Bright orange */
  --accent-blue: hsl(200, 100%, 60%); /* Bright blue */
  --text-primary: hsl(220, 30%, 25%); /* Deep blue text */
  --text-secondary: hsl(220, 20%, 50%); /* Medium blue text */
}

/* Dark theme removed - optimized for light theme only */

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-family: 'Inter', sans-serif;
    scroll-behavior: smooth;
    overflow-x: hidden;
    background-image: 
      radial-gradient(circle at 20% 80%, hsla(280, 100%, 90%, 0.3) 0%, transparent 50%),
      radial-gradient(circle at 80% 20%, hsla(120, 100%, 90%, 0.3) 0%, transparent 50%),
      radial-gradient(circle at 40% 40%, hsla(45, 100%, 90%, 0.2) 0%, transparent 50%);
  }

  html {
    overflow-x: hidden;
  }
}

@layer components {
  .mobile-padding {
    @apply px-4 sm:px-6 lg:px-8;
  }

  /* Childlike decorative elements */
  .playful-shadow {
    box-shadow: 
      0 8px 25px -8px hsla(280, 100%, 70%, 0.3),
      0 4px 12px -4px hsla(120, 60%, 45%, 0.2),
      0 2px 6px -2px hsla(45, 100%, 60%, 0.2);
  }

  .rainbow-border {
    border: 3px solid;
    border-image: linear-gradient(45deg, 
      hsl(280, 100%, 70%), 
      hsl(200, 100%, 60%), 
      hsl(120, 60%, 45%), 
      hsl(45, 100%, 60%), 
      hsl(0, 80%, 65%)
    ) 1;
    border-radius: 1.5rem;
  }

  .wiggle-animation {
    animation: wiggle 2s ease-in-out infinite;
  }

  @keyframes wiggle {
    0%, 100% { transform: rotate(0deg); }
    25% { transform: rotate(1deg); }
    75% { transform: rotate(-1deg); }
  }

  .bounce-animation {
    animation: bounce-gentle 3s ease-in-out infinite;
  }

  @keyframes bounce-gentle {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-5px); }
  }

  .container-safe {
    @apply max-w-7xl mx-auto px-4 sm:px-6 lg:px-8;
    width: 100%;
    box-sizing: border-box;
  }
  .gradient-border {
    background: linear-gradient(135deg, var(--accent-green), hsl(142, 76%, 28%));
    border-radius: 12px;
    padding: 2px;
  }

  .gradient-border-content {
    background: var(--light-primary);
    border-radius: 10px;
    height: 100%;
  }

  .cta-gradient {
    background: linear-gradient(135deg, var(--accent-red), hsl(0, 84%, 70%));
  }

  .cta-gradient:hover {
    background: linear-gradient(135deg, hsl(0, 84%, 70%), var(--accent-red));
  }

  .animate-fade-in {
    animation: fadeIn 0.6s ease-out;
  }

  .animate-slide-up {
    animation: slideUp 0.8s ease-out;
  }

  .animate-pulse-slow {
    animation: pulse 3s infinite;
  }

  /* Mobile responsive adjustments */
  .mobile-padding {
    padding-left: 1rem;
    padding-right: 1rem;
  }

  @media (max-width: 640px) {
    .mobile-text-scale {
      font-size: 1.25rem;
      line-height: 1.75rem;
    }

    .mobile-grid-fix {
      grid-template-columns: repeat(2, 1fr);
      gap: 0.75rem;
    }

    .mobile-button-full {
      width: 100%;
      font-size: 1rem;
      padding: 1rem 1.5rem;
    }
  }
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideUp {
  from { 
    opacity: 0;
    transform: translateY(30px);
  }
  to { 
    opacity: 1;
    transform: translateY(0);
  }
}

